import { type Metadata } from "next";
import { Geist, Geist_Mono } from "next/font/google";
import "./globals.css";
import { headers } from 'next/headers';
import { TestFakesProvider } from './testing/TestFakesProvider';
import {
  getTestFakesFrom
} from './testing/TestFakesAdapterService';
import Link from 'next/link';

const geistSans = Geist({
  variable: "--font-geist-sans",
  subsets: ["latin"],
});

const geistMono = Geist_Mono({
  variable: "--font-geist-mono",
  subsets: ["latin"],
});

export const metadata: Metadata = {
  title: "Aero logic",
  description: "Generated by create next app",
};

export default async function RootLayout({
  children,
}: Readonly<{
  children: React.ReactNode;
}>) {
  const headersList = await headers();

  return (
    <html lang="en">
      <body
        className={`${geistSans.variable} ${geistMono.variable} antialiased`}
      >
      <TestFakesProvider
        fakes={getTestFakesFrom(headersList)}>
        <div className="container mx-auto h-screen" style={{maxWidth: '640px'}}>
          <div className="pt-8 md:pt-16 h-full">
            <div
              className="py-4 px-10 h-full bg-white rounded-t-md">
              <header className="pb-8">
                <Link href="/">
                  <img className="-ml-2"
                       alt="Aero Logic"
                       src="data:image/png;base64,"
                  />
                </Link>
              </header>
              {children}
            </div>
          </div>
        </div>
      </TestFakesProvider>
      </body>
    </html>
  );
}
